        function alert(msg,duration)
        {
            let tooltip = document.getElementById("alertCopy");
            let el = document.createElement("div");
            tooltip.appendChild(el);
            el.innerHTML += '<div id="child" class="alert alert-success"><i class="voyager-check" style="font-size:18px;"></i>&nbsp&nbsp&nbsp<strong>'+ msg +'</strong></div>';
            setTimeout(function(){
                let fadeEffect = setInterval(function () {
                    if (!el.style.opacity) {
                        el.style.opacity = 1;
                    }
                    if (el.style.opacity > 0) {
                        el.style.opacity -= 0.06;
                    } else {
                        clearInterval(fadeEffect);
                    }
                    setTimeout(function(){
                        el.parentNode.removeChild(el);
                    },duration);
                }, 200);
            },4000);
        }
